                                                          FROM   hrm_office_setup
                                                    START WITH   office_cd =
                                                                    p_office_cd
                                                    CONNECT BY   PRIOR office_cd =
                                                                    control_office_cd)))
                      GROUP BY   a.agent_id,
                                 a.office_cd,
                                 a.bill_no,
                                 a.bill_date,
                                 a.remarks,
                                 a.ins_tp_cd,
                                 o.office_cd,
                                 o.office_add1,
                                 o.office_nm
                      UNION
                        SELECT   f_emp_id (a.emp_gid) emp_id,
                                 NULL idra_cd,
                                 f_emp_nm (a.emp_gid) agent_nm,
                                 f_emp_type_nm (a.emp_gid) desig_nm,
                                 f_emp_type_cd (a.emp_gid) desig_cd,
