         l_tab.opening_bal := v_opening_bal;
         --v_pre_payment_amt - (v_jv_amt + v_mr_amt);
         l_tab.adv_payment := i.pay_amt;
         l_tab.total_adv_payment :=
              v_opening_bal      --(v_pre_payment_amt - (v_jv_amt + v_mr_amt))
