         v_date_from := p_date_from;

         FOR i IN
            (SELECT   y.glac_sl_no, y.cntrl_glac_sl_no, y.glac_no, y.glac_nm,
                      y.account_type, NVL (SUM (bal), 0) bal
                 FROM (SELECT   glac_sl_no, SUM (debit) - SUM (credit) bal
                           FROM (SELECT m.glac_sl_no,
                                        CASE
                                           WHEN vouch_st = '0'
