                                DECODE (a.dr_cr, 'D', a.trans_amt, 0)
                                   debit_amt,
                                b.narration,
                                b.vouch_cd,
                                a.vouch_no vouch_no_actual
                         FROM   cpf_gl_trans_dtl a,
                                cpf_gl_trans_mst b,
                                cpf_cash_book_divert d
                        WHERE   b.office_cd IN
                                      (SELECT   DISTINCT
                                                DECODE (p_check,
