                                                 START WITH   office_cd =
                                                                 p_office_cd)
                                                y)
                                AND b.vouch_no = NVL (p_vouch_no, b.vouch_no)
                                AND NVL (b.cost_center, 'X') =
                                      NVL (p_cost_center,
                                           NVL (b.cost_center, 'X'))
                                AND a.glac_sl_no = v_glac_sl_no
                                AND TRUNC (b.vouch_dt) BETWEEN pdate_from
