                           || ' ('
                           || f_emp_nm (e.emp_gid)
                           || ')'
                      FROM hrm_employee
                     WHERE emp_gid = e.emp_gid)*/
                   ---Musha on 30.10.2017
                   (SELECT   DECODE (
                                applicant_tp,
                                '1',
                                   rep_f_employee (e.emp_gid)
                                || ' ('
                                || f_emp_id (e.emp_gid)
                                || ')',
                                applicant_name
                             )
                      FROM   cpf_final_settl_appl_dtl
                     WHERE   appl_no = s.appl_no AND sl_no = s.sl_no)
                      paid_to,
                   m.narration,
                   d.trans_amt,
                   NVL (d.chq_no, s.pay_order_no) chq_no,
                   NVL (d.chq_dt, s.pay_order_date) chq_dt,
                   LTRIM (
                      RTRIM (
