                   AND d.dr_cr = 'D'
                   AND s.voucher_no = NVL (p_vouch_no, s.voucher_no)
                   AND m.cost_center = NVL (p_cost_center, m.cost_center)
                   AND m.vouch_dt BETWEEN NVL (p_date_fr, m.vouch_dt)
                                      AND  NVL (p_date_to, m.vouch_dt))
