         l_tab.glac_no := i.glac_no;
         l_tab.vouch_nm := i.vouch_nm;
         l_tab.debit_amt := i.debit;
         l_tab.credit_amt := i.credit;
         PIPE ROW (l_tab);
      END LOOP;

      RETURN;
