                           FROM ins_p_summery cs
                          WHERE cs.servicing_off =
                                    NVL (p_servicing_off_cd, cs.servicing_off)
                            AND cs.sales_off =
                                            NVL (p_sales_off_cd, cs.sales_off)
                            AND cs.branch_off =
