                                           '01', 1,
                                           '02', 2,
                                           '03', 4,
                                           '04', 12
                                          )
                            AND comm_date BETWEEN p_com_date_from
                                              AND p_com_date_to
                            AND (   (    p_ins_tp_cd IS NOT NULL
                                     AND ins_tp_cd = p_ins_tp_cd
                                    )
                                 OR (p_ins_tp_cd IS NULL)
                                )
                       GROUP BY DECODE (cs.servicing_off,
                                        '00000', v_office_cd,
                                        cs.servicing_off
                                       ),
                                DECODE (cs.sales_off,
                                        '00000', DECODE (cs.servicing_off,
                                                         '00000', v_office_cd,
                                                         cs.servicing_off
                                                        ),
                                        cs.sales_off
                                       ),
                                DECODE (cs.branch_off,
                                        '00000', DECODE
