                                                            ),
                                                    cs.sales_off
                                                   ),
                                        cs.branch_off
                                       )) s
                WHERE f.servicing_off = s.servicing_off(+)
                  AND f.sales_off = s.sales_off(+)
                  AND f.branch_off = s.branch_off(+)
             ORDER BY f.servicing_off, f.sales_off, f.branch_off)
         LOOP
            l_biz.servicing_off := i.servicing_off;
