   IS
      v_payment_receipt      payment_receipt_rec;
      v_sb_instlno           VARCHAR2 (3);

      TYPE cur_tp IS REF CURSOR;

      v_cur                  cur_tp;
      ---------------------------
      v_due_prem_amt         NUMBER (12, 2) := 0;
      v_due_prem_late_fee    NUMBER (12, 2) := 0;
      v_paid_prem_late_fee   NUMBER (12, 2) := 0;

