                                 p_coll_dt_to      DATE,
                                 p_pr_date_from    DATE,
                                 p_pr_date_to      DATE)
      RETURN tp_month_wise_coll_tab
      PIPELINED
   AS
      v_unadj_policy        tp_month_wise_coll;
      v_partial_coll        NUMBER (12, 2);
      v_total_coll          NUMBER (12, 2);
      v_no_of_inst          NUMBER;
