                         OR (p_ins_tp_cd IS NULL AND 1 = 1))
         GROUP BY   ar.active_office_cd, ar.receive_date, ar.pgid;

      CURSOR c_oth_coll
      IS
         SELECT   r.pgid,
                  r.receive_id,
                  r.active_office_cd,
