            v_ren_prem_amount := 0;

            IF rec_c_oth_coll.pay_mode_cd = '01'
            THEN
               IF i = 1
               THEN
                  v_first_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
                  v_def_prem_amount := 0;
               ELSIF i = 2
               THEN
                  v_sec_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
               ELSE
                  v_ren_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
               END IF;
            ELSIF rec_c_oth_coll.pay_mode_cd = '02'
            THEN
               IF i = 1
               THEN
                  v_first_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
               ELSIF i = 2
               THEN
                  v_def_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
               ELSIF i IN (3, 4)
               THEN
                  v_sec_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
               ELSE
                  v_ren_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
               END IF;
            ELSIF rec_c_oth_coll.pay_mode_cd = '03'
            THEN
