               ELSIF i >= 2 AND i <= 4
               THEN
                  v_def_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
               ELSIF i >= 5 AND i <= 8
               THEN
                  v_sec_prem_amount :=
