                  v_first_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
               ELSIF i >= 2 AND i <= 12
               THEN
                  v_def_prem_amount :=
                     ROUND ( (v_total_coll / v_no_of_inst), 2);
