                        fun_prem_coll_receipt_no (r.partner_code,
                                                  r.partner_transid,
                                                  r.active_office_cd,
                                                  r.receive_date,
                                                  r.coll_slip_no,
                                                  r.coll_sl_no)
                           receipt_no,
                        NVL (r.coll_slip_rec_dt, r.receive_date) receipt_dt,
                        r.adj_slip_no,
                        (r.installment_no_to - installment_no_from + 1)
                           no_of_instl,
                        r.tot_premium_amt,
                        (CASE
                            WHEN     NVL (r.status, '0') = '1'
                                 AND NVL (r.return_st, '0') = '0'
                                 AND NVL (i.return_st, '0') = '0'
