                              END LOOP;*/
               ---Musha on 25-11-2014
               v_partial_receipt_no := RTRIM (v_partial_receipt_no, ', ');
               v_all_par_coll_amount := RTRIM (v_all_par_coll_amount, ', ');
               v_receipt_no := v_receipt_no || ', ' || v_partial_receipt_no;
