                        v_coll_amount,
                        v_adv_status,
                        v_adjusted_method,
                        v_receipt_no,
                        v_receipt_date,
                        v_adj_slip_no,
                        v_no_of_instl,
                        v_tot_premium_amt,
                        v_adj_prem_amt,
                        v_unadj_prem_amt,
                        v_return_prem_amt
                 FROM   acc_receivables r
                WHERE   r.pgid = i.pgid AND r.receive_id = i.proposer_pk;

               IF v_no_of_instl > 0
               THEN
