                  AND fun_locked_policy_auth (p.pgid) = '1'
         /*AND (p_report_option = '0'
                 OR (    p_report_option = '1'
                     AND (   NOT EXISTS (
                                      SELECT 1
                                        FROM acc_cheque_print_info t
                                       WHERE t.pgid = p.pgid
