                                                     p.commutation_dt)
                  AND ( (NVL (p_descendant, 'N') = 'N'
                         AND s.servicing_off =
                               NVL (p_office_cd, s.servicing_off))
                       OR (NVL (p_descendant, 'N') = 'Y'
                           AND EXISTS
