      CLOSE c_fpr_coll;

      OPEN c_oth_coll;

      LOOP
         FETCH c_oth_coll INTO   rec_c_oth_coll;

         EXIT WHEN c_oth_coll%NOTFOUND;

         BEGIN
              SELECT   SUM (coll_amount)
                INTO   v_partial_coll
                FROM   acc_partial_receivables p, acc_partial_receivables_ref r
