                                                                                                                'MM'
                                                                                                             ))
                                                                                           THEN
                                                                                              12
                                                                                           ELSE
                                                                                              0
                                                                                        END)
                                                                                    / DECODE (
                                                                                         pay_mode_cd,
                                                                                         '01',
                                                                                         12,
                                                                                         '02',
                                                                                         6,
                                                                                         '03',
                                                                                         3,
                                                                                         '04',
                                                                                         1
                                                                                      )))
                                                                            + 1
                                                                      END
                                                                         due_installment,
                                                                      installment_premium
                                                               FROM   (SELECT   s.pgid,
                                                                                s.comm_date,
                                                                                s.last_paid_date,
                                                                                s.last_paid_inst_no,
                                                                                s.maturity_date,
                                                                                s.pay_mode_cd,
