            v_coll_rec.renewal_full_coll_prem := i.renewal_full_coll_prem;
            v_coll_rec.renewal_partial_coll_prem :=
               i.renewal_partial_coll_prem;
            PIPE ROW (v_coll_rec);
         END LOOP;
      END IF;
   END fun_idra_lapse_ratio;
END pkg_val_idra_lapse_report;
