BEGIN
   INSERT INTO hrm_agent_off_incharge_hist (office_code,
                                            incharge_emp_gid,
                                            desig_cd,
                                            start_dt,
                                            end_dt,
                                            mobile,
                                            status,
                                            i_usr,
                                            i_dt,
                                            u_usr,
                                            u_dt)
